mapper.select:
SELECT * FROM TB_SYS_MENU WHERE LEVEL = '2'
AND PARENT_TYPE = #{parentType}
ORDER BY MENU_ORDER

mapper.insert:
INSERT
INTO TB_SYS_MENU
(
    MENU_NM
    , URL
    , LEVEL
    , MENU_ORDER
    , PARENT_ID
    , PARENT_TYPE
    , CHILD_TYPE
    , REGIST_DT
)
VALUES
(
    #{menuNm}
    , #{url}
    , #{level}
    , #{menuOrder}
    , #{parentId}
    , #{parentType}
    , #{childType}
    , date_format(now(), '%Y%m%d%H%i%s')
)

mapper.update:
UPDATE TB_SYS_MENU
SET
    MENU_NM = #{menuNm}
    , URL = #{url}
    , LEVEL = #{level}
    , MENU_ORDER = #{menuOrder}
    , PARENT_ID = #{parentId}
    , PARENT_TYPE = #{parentType}
    , CHILD_TYPE = #{childType}
WHERE ID = #{id}
